package com.org.pogram;

public class PrintTraingle {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		for(int i=0;i<10;i++){
			for(int j=i;j<10;j++){
				System.out.print(" ");
			}
				for(int j=0;j<=i;j++){
					System.out.print(" *");
				}
				
			System.out.println();	
		
		}
		
	
		
	}

}
